changeSet.sql:
create table `nation`
(
    `id`   int(11) not null auto_increment,
    `name` varchar(32) default null,
    primary key (`id`)
) comment = '民族表';

create table `politics_status`
(
    `id`   int(11) not null auto_increment,
    `name` varchar(32) default null,
    primary key (`id`)
) comment = '政治面貌表';
